import java.util.*;
public class typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String s="10";
int n=Integer.parseInt(s);
System.out.println("the casted string is "+n);
float f=4.55f;
int k=(int)f;
System.out.println("the float value is "+f+" the integer value is "+k);

	}

}
